import gql from 'graphql-tag';
import { useQuery, useMutation } from '@apollo/react-hooks';
import { Card, ResourceList, Stack, TextStyle } from '@shopify/polaris';


const CREATE_SCRIPT_TAG = gql`
    mutation scriptTagCreate($input: ScriptTagInput!) {
        scriptTagCreate(input: $input) {
            scriptTag {
                id
            }
            userErrors {
                field
                message
            }
        }
    }
`;

const QUERY_SCRIPTTAGS = gql`
    query {
        scriptTags(first: 5) {
            edges {
                node {
                    id
                    src
                    displayScope
                }
            }
        }
    }
`;

const DELETE_SCRIPTTAG = gql`
    mutation scriptTagDelete($id: ID!) {
        scriptTagDelete(id: $id) {
            deletedScriptTagId
            userErrors {
                field
                message
            }
        }
    }
`;


function ScriptPage() {

  const [createScripts] = useMutation(CREATE_SCRIPT_TAG);
  const [deleteScripts] = useMutation(DELETE_SCRIPTTAG);
  const { loading, error, data } = useQuery(QUERY_SCRIPTTAGS);


  if (loading) return <div>Loadingâ€¦</div>;
  if (error) return <div>{error.message}</div>;

  return (
    <Card>
      <h1>These are the script tags right now:</h1>
      <button
        type='submit' onClick={() => {
          createScripts({
            variables: {
              input: {
                src: "https://ad8de5a8.ngrok.io/test-script.js",
                displayScope: "ALL"
              }
            },
            refetchQueries: [{ query: QUERY_SCRIPTTAGS }]
          })
        }}
      >
        Create Script Tag
            </button>

      {/* {data.scriptTags.edges.map(item => {

                return (
                    <div key={item.node.id}>
                        <p>{item.node.id}</p>
                        <button type='submit' onClick={() => {
                            deleteScripts({
                                variables: {
                                    id: item.node.id
                                },
                                refetchQueries: [{ query: QUERY_SCRIPTTAGS }]
                            })
                        }}>
                            Delete Script Tag
                        </button>
                    </div>
                )
            })} */}
      <ResourceList
        showHeader
        resourceName={{ singular: 'Script', plural: 'Scripts' }}
        items={data.scriptTags.edges}
        renderItem={item => {
          return (
            <ResourceList.Item
              id={item.id}
            >
              <Stack>
                <Stack.Item>
                  <p>{item.node.id}</p>
                </Stack.Item>
                <Stack.Item>
                  <button type='submit' onClick={() => {
                    deleteScripts({
                      variables: {
                        id: item.node.id
                      },
                      refetchQueries: [{ query: QUERY_SCRIPTTAGS }]
                    })
                  }}>
                    Delete Script Tag
                                    </button>
                </Stack.Item>
              </Stack>
            </ResourceList.Item>
          )
        }}

      />
    </Card>
  )
}

export default ScriptPage;
