import {useState, useEffect} from 'react';
import Switch from 'react-ios-switch';
import axios from 'axios';
import {useSelector, useDispatch} from 'react-redux';
import {changeConditionsAction} from '../../redux/actions/conditionals/changeConditionals';

interface IContitionAuth {
  address: boolean;
  ammount: boolean;
}

const ConditionsGlobalId = () => {
  const [conditionAuth, setConditionAuth] = useState<IContitionAuth>({
    address: false,
    ammount: false
  });
  const dispatch = useDispatch();
  const userInfo = useSelector((state: any) => state.user);
  console.log('Estado desde redux', userInfo.user.settings);
  const settingsUser = userInfo?.user?.settings;

  const handleChangeconditions = (dataChange: any) => {
    console.log(dataChange);
    dispatch(changeConditionsAction());
  };

  const handleChangeConditionsAddress =  async (condition: boolean) => {
    const valor = {
      ...conditionAuth,
      address: condition,
    };
    try {
      await axios.post('/api/conditions', valor);
      setConditionAuth({...conditionAuth, address: condition});
    } catch (error) {
      console.log(error);
    }
  };

   const handleChangeConditionsAmmount =  async (condition: boolean) => {
    const valor = {
      ...conditionAuth,
      ammount: condition,
    };
    try {
      await axios.post('/api/conditions', valor);
      setConditionAuth({...conditionAuth, ammount: condition});
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="pb-10 pl-20">
      <div className="flex justify-between my-10 items-center">
        <h4 className="text-2xl w-11/12 pr-10"
          >Require ID verification for orders when <b>Billing Address</b> does not match <b>Shipping Address?</b>
        </h4>
        <Switch
          onChange={() => handleChangeconditions({...settingsUser, })}
          checked={userInfo?.user?.settings?.address}
          onColor="#0D51FF"
        />
      </div>
      <div className="flex justify-between my-10 items-center">
        <div className="flex items-center">
          <h4 className="text-2xl">Require ID verification for orders above $ </h4> 
          <input
            onChange={() => console.log('Cambiando el valor del monto máximo')}
            value={userInfo?.user?.limit_ammount.toString()}
            className="w-24 border-2 border-blue-500 text-blue-500 text-center py-2 rounded-lg ml-3 font-semibold"
          />
        </div>
        <Switch
          onChange={() => handleChangeconditions({...settingsUser, })}
          checked={userInfo?.user?.settings?.ammount}
          onColor="#0D51FF"
        />
      </div>
    </div>
  )
}

export default ConditionsGlobalId
